/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */
import { ColourPalette } from "./components/ColourPalette";
import staplesRed from "../../public/images/staples-red.svg";
import white from "../../public/images/white.svg";
import charcoal from "../../public/images/charcoal.svg";
import midGray from "../../public/images/mid-gray.svg";
import lightGray from "../../public/images/light-gray.svg";
import lightestGray from "../../public/images/lightest-gray.svg";
import chartreuse from "../../public/images/chartreuse.svg";
import cork from "../../public/images/cork.svg";
import darkBlue from "../../public/images/dark-blue.svg";
import blue from "../../public/images/blue.svg";
import lightestBlue from "../../public/images/lightest-blue.svg";
import black from "../../public/images/black.svg";
import red from "../../public/images/red.svg";
import yellow from "../../public/images/yellow.svg";
import green from "../../public/images/green.svg";
import tertiary from "../../public/images/tertiary.svg";

const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h1 {...{
      "id": "colours"
    }}>{`Colours`}</h1>
    <h4 {...{
      "id": "remaining-consistent-across-staples-various-digital-products-whether-it-be-applications-subsites-or-other-experiences-requires-rules-around-colour-usage"
    }}>{`Remaining consistent across Staples’ various digital products, whether it be applications, subsites or other experiences, requires rules around colour usage.`}</h4>
    <h2 {...{
      "id": "colour-palette"
    }}>{`Colour Palette`}</h2>
    <p>{`The Staples colour palette is comprised of Primary, Secondary and Tertiary colours. Each created with a specific purpose in mind to help shape the brand we’ve all come to know.`}</p>
    <ColourPalette mdxType="ColourPalette" />
    <h2 {...{
      "id": "applying-colour"
    }}>{`Applying Colour`}</h2>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": "left"
          }}>{`Colour`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`Usage`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><img src={staplesRed} /></td>
          <td parentName="tr" {...{
            "align": "left"
          }}><strong parentName="td">{`Staples Red`}</strong>{` serves as the `}<strong parentName="td">{`primary call-to-action colour`}</strong>{` across all Staples products and experiences. It is only to be used sparingly and with intent. Reserve its use to highlight critically important information. Never to be used in headings or body copy.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><img src={white} /></td>
          <td parentName="tr" {...{
            "align": "left"
          }}><strong parentName="td">{`White`}</strong>{` is to be used as the `}<strong parentName="td">{`default background`}</strong>{`. It’s also useful for meeting contrast requirements when there is a need to overlay text on top of solid colour elements.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><img src={charcoal} /></td>
          <td parentName="tr" {...{
            "align": "left"
          }}><strong parentName="td">{`Charcoal`}</strong>{` is best suited for `}<strong parentName="td">{`headings`}</strong>{` and `}<strong parentName="td">{`body copy`}</strong>{`. It can be used as a background with white text on occasion.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><img src={midGray} /></td>
          <td parentName="tr" {...{
            "align": "left"
          }}><strong parentName="td">{`Mid-Gray`}</strong>{` is intended for `}<strong parentName="td">{`pull quotes, sub-headings, lead text, list bullets, form input text, icons, tertiary links and buttons`}</strong>{`, to name a few.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><img src={lightGray} /></td>
          <td parentName="tr" {...{
            "align": "left"
          }}><strong parentName="td">{`Light-Gray`}</strong>{` is best used as the main colour for `}<strong parentName="td">{`element outlines, dividers, disabled text, buttons or states`}</strong>{`.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><img src={lightestGray} /></td>
          <td parentName="tr" {...{
            "align": "left"
          }}><strong parentName="td">{`Lightest-Gray`}</strong>{` is best used as a `}<strong parentName="td">{`background`}</strong>{` to highlight important information or where a user is able to interact with elements. It should `}<strong parentName="td">{`never be used for text`}</strong>{`.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><img src={chartreuse} /></td>
          <td parentName="tr" {...{
            "align": "left"
          }}><strong parentName="td">{`Chartreuse`}</strong>{` is intended to be used sparingly to `}<strong parentName="td">{`accent deals or promotions`}</strong>{`.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><img src={cork} /></td>
          <td parentName="tr" {...{
            "align": "left"
          }}><strong parentName="td">{`Cork`}</strong>{` is used to `}<strong parentName="td">{`accent long running promotions`}</strong>{` and as a solid colour background.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><img src={darkBlue} /></td>
          <td parentName="tr" {...{
            "align": "left"
          }}><strong parentName="td">{`Dark Blue`}</strong>{` is predominantly used for `}<strong parentName="td">{`text links`}</strong>{` and `}<strong parentName="td">{`secondary buttons`}</strong>{`.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><img src={blue} /></td>
          <td parentName="tr" {...{
            "align": "left"
          }}><strong parentName="td">{`Blue`}</strong>{` is to be used to `}<strong parentName="td">{`accent new or featured products`}</strong>{` and as a solid colour background.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><img src={lightestBlue} /></td>
          <td parentName="tr" {...{
            "align": "left"
          }}><strong parentName="td">{`Lightest-Blue`}</strong>{` is ideal for `}<strong parentName="td">{`hover states`}</strong>{` within table elements, dropdowns, menus and lists.`}</td>
        </tr>
      </tbody>
    </table>
    <hr></hr>
    <h2 {...{
      "id": "usage"
    }}>{`Usage`}</h2>
    <h4 {...{
      "id": "colour-in-the-staples-design-system-is-managed-using-colour-tokens-this-allows-us-to-remain-consistent-across-all-our-digital-interfaces-and-applications"
    }}>{`Colour in the Staples Design System is managed using colour tokens. This allows us to remain consistent across all our digital interfaces and applications.`}</h4>
    <h3 {...{
      "id": "colour-tokens"
    }}>{`Colour Tokens`}</h3>
    <p>{`Colour in the Staples Design System is managed using colour tokens. Tokens are a method of noting colour by role or usage, independent of the actual colour value (hex). For instance, instead of having to code all instances of a primary button in a UI to be `}<inlineCode parentName="p">{`#CC0000`}</inlineCode>{`, the primary button component specifies the token `}<inlineCode parentName="p">{`$brand-red`}</inlineCode>{` for these buttons, and that token is specified in a separate file to be `}<inlineCode parentName="p">{`#CC0000`}</inlineCode>{`.`}</p>
    <p>{`A single token can be associated to multiple roles, but only if the colour value is used consistently across those roles. This allows for uniform colour application and efficient global changes while maintaining full styling flexibility.`}</p>
    <h3 {...{
      "id": "neutral-colours"
    }}>{`Neutral Colours`}</h3>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`Tokens`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Value`}</th>
          <th parentName="tr" {...{
            "align": null
          }}></th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`$white`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`#FFFFFF`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><img style={{
              width: 40
            }} src={white} /></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`$lightest-gray`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`#F3F3F2`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><img style={{
              width: 40
            }} src={lightestGray} /></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`$light-gray`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`#D9D9D6`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><img style={{
              width: 40
            }} src={lightGray} /></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`$mid-gray`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`#747679`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><img style={{
              width: 40
            }} src={midGray} /></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`$dark-gray`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`#444444`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><img style={{
              width: 40
            }} src={charcoal} /></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`$black`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`#000000`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><img style={{
              width: 40
            }} src={black} /></td>
        </tr>
      </tbody>
    </table>
    <h3 {...{
      "id": "brand-colours"
    }}>{`Brand Colours`}</h3>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`Tokens`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Value`}</th>
          <th parentName="tr" {...{
            "align": null
          }}></th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`$primary`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`#CC0000`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><img style={{
              width: 40
            }} src={staplesRed} /></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`$secondary`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`#198294`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><img style={{
              width: 40
            }} src={darkBlue} /></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`$tertiary`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`#747679`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><img style={{
              width: 40
            }} src={midGray} /></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`$charcoal`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`#444444`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><img style={{
              width: 40
            }} src={charcoal} /></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`$blue`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`#9BD3DD`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><img style={{
              width: 40
            }} src={blue} /></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`$lightest-blue`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`#EFF6F7`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><img style={{
              width: 40
            }} src={lightestBlue} /></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`$chartreuse`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`#D2D420`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><img style={{
              width: 40
            }} src={chartreuse} /></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`$cork`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`#DDCBA4`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><img style={{
              width: 40
            }} src={cork} /></td>
        </tr>
      </tbody>
    </table>
    <h3 {...{
      "id": "system-colours"
    }}>{`System Colours`}</h3>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`Role`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Value`}</th>
          <th parentName="tr" {...{
            "align": null
          }}></th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`$error`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`#E70000`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><img style={{
              width: 40
            }} src={red} /></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`$warning`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`#F9A424`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><img style={{
              width: 40
            }} src={yellow} /></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`$success`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`#18A654`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><img style={{
              width: 40
            }} src={green} /></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`$info`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`#747679`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><img style={{
              width: 40
            }} src={tertiary} /></td>
        </tr>
      </tbody>
    </table>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
