/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */


const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1 {...{
      "id": "tooltips"
    }}>{`Tooltips`}</h1>
    <h6 {...{
      "id": "tooltipoptions"
    }}>{`\\$().tooltip(options)`}</h6>
    <p>{`Attaches a tooltip handler to an element collection.`}</p>
    <h6 {...{
      "id": "tooltipshow"
    }}>{`.tooltip(‘show’)`}</h6>
    <p>{`Reveals an element’s tooltip. Returns to the caller before the tooltip has actually been shown (i.e. before the `}<inlineCode parentName="p">{`shown.bs.tooltip`}</inlineCode>{` event occurs). This is considered a “manual” triggering of the tooltip. Tooltips with zero-length titles are never displayed.`}</p>
    <pre><code parentName="pre" {...{}}>{`$('#element').tooltip('show')
`}</code></pre>
    <h6 {...{
      "id": "tooltiphide"
    }}>{`.tooltip(‘hide’)`}</h6>
    <p>{`Hides an element’s tooltip. Returns to the caller before the tooltip has actually been hidden (i.e. before the `}<inlineCode parentName="p">{`hidden.bs.tooltip`}</inlineCode>{` event occurs). This is considered a “manual” triggering of the tooltip.`}</p>
    <pre><code parentName="pre" {...{}}>{`$('#element').tooltip('hide')
`}</code></pre>
    <h6 {...{
      "id": "tooltiptoggle"
    }}>{`.tooltip(‘toggle’)`}</h6>
    <p>{`Toggles an element’s tooltip. Returns to the caller before the tooltip has actually been shown or hidden (i.e. before the `}<inlineCode parentName="p">{`shown.bs.tooltip`}</inlineCode>{` or `}<inlineCode parentName="p">{`hidden.bs.tooltip`}</inlineCode>{` event occurs). This is considered a “manual” triggering of the tooltip.`}</p>
    <pre><code parentName="pre" {...{}}>{`$('#element').tooltip('toggle')
`}</code></pre>
    <h6 {...{
      "id": "tooltipdispose"
    }}>{`.tooltip(‘dispose’)`}</h6>
    <p>{`Hides and destroys an element’s tooltip. Tooltips that use delegation (which are created using the selector option) cannot be individually destroyed on descendant trigger elements.`}</p>
    <pre><code parentName="pre" {...{}}>{`$('#element').tooltip('dispose')
`}</code></pre>
    <h6 {...{
      "id": "tooltipenable"
    }}>{`.tooltip(‘enable’)`}</h6>
    <p>{`Gives an element’s tooltip the ability to be shown. Tooltips are enabled by default.`}</p>
    <pre><code parentName="pre" {...{}}>{`$('#element').tooltip('enable')
`}</code></pre>
    <h6 {...{
      "id": "tooltipdisable"
    }}>{`.tooltip(‘disable’)`}</h6>
    <p>{`Removes the ability for an element’s tooltip to be shown. The tooltip will only be able to be shown if it is re-enabled.`}</p>
    <pre><code parentName="pre" {...{}}>{`$('#element').tooltip('disable')
`}</code></pre>
    <h6 {...{
      "id": "tooltiptoggleenabled"
    }}>{`.tooltip(‘toggleEnabled’)`}</h6>
    <p>{`Toggles the ability for an element’s tooltip to be shown or hidden.`}</p>
    <pre><code parentName="pre" {...{}}>{`$('#element').tooltip('toggleEnabled')
`}</code></pre>
    <h6 {...{
      "id": "tooltipupdate"
    }}>{`.tooltip(‘update’)`}</h6>
    <p>{`Updates the position of an element’s tooltip.`}</p>
    <pre><code parentName="pre" {...{}}>{`$('#element').tooltip('update')
`}</code></pre>
    <h3 {...{
      "id": "events"
    }}>{`Events`}</h3>
    <table class="table table-bordered table-striped">
  <thead>
    <tr>
      <th style="width: 150px;">Event Type</th>
      <th>Description</th>
    </tr>
  </thead>
  <tbody>
    <tr>
      <td>show.bs.tooltip</td>
      <td>This event fires immediately when the <code>show</code> instance method is called.</td>
    </tr>
    <tr>
      <td>shown.bs.tooltip</td>
      <td>This event is fired when the tooltip has been made visible to the user (will wait for CSS transitions to complete).</td>
    </tr>
    <tr>
      <td>hide.bs.tooltip</td>
      <td>This event is fired immediately when the <code>hide</code> instance method has been called.</td>
    </tr>
    <tr>
      <td>hidden.bs.tooltip</td>
      <td>This event is fired when the tooltip has finished being hidden from the user (will wait for CSS transitions to complete).</td>
    </tr>
    <tr>
      <td>inserted.bs.tooltip</td>
      <td>This event is fired after the <code>show.bs.tooltip</code> event when the tooltip template has been added to the DOM.</td>
    </tr>
  </tbody>
    </table>
    <pre><code parentName="pre" {...{}}>{`$('#myTooltip').on('hidden.bs.tooltip', function () {
    // do something...
})
`}</code></pre>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
