/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */


const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1 {...{
      "id": "checkboxes"
    }}>{`Checkboxes`}</h1>
    <p>{`Default checkboxes and radios are improved upon with the help of `}<inlineCode parentName="p">{`.form-check`}</inlineCode>{`, a single class for both input types that improves the layout and behavior of their HTML elements. Checkboxes are for selecting one or several options in a list, while radios are for selecting one option from many.`}</p>
    <p>{`Checkboxes and radios use are built to support HTML-based form validation and provide concise, accessible labels. As such, our `}<inlineCode parentName="p">{`<input>`}</inlineCode>{`s and `}<inlineCode parentName="p">{`<label>`}</inlineCode>{`s are sibling elements as opposed to an `}<inlineCode parentName="p">{`<input>`}</inlineCode>{` within a `}<inlineCode parentName="p">{`<label>`}</inlineCode>{`. This is slightly more verbose as you must specify id and for attributes to relate the `}<inlineCode parentName="p">{`<input>`}</inlineCode>{` and `}<inlineCode parentName="p">{`<label>`}</inlineCode>{`.`}</p>
    <h3 {...{
      "id": "examples-of-documentation---checkboxes"
    }}>{`Examples oF Documentation - Checkboxes`}</h3>
    <pre><code parentName="pre" {...{}}>{`<div class="form-check">
    <input class="form-check-input" type="checkbox" name="exampleCustomCheckbox" id="customCheckbox1" value="option1" checked>
    <label class="form-check-label" for="customCheckbox1">
        Default checkbox
    </label>
</div>
<div class="form-check">
    <input class="form-check-input" type="checkbox" name="exampleCustomCheckbox" id="customCheckbox2" value="option2">
    <label class="form-check-label" for="customCheckbox2">
        Second default checkbox
</label>
</div>
`}</code></pre>
    <h2 {...{
      "id": "disabled-checkboxes"
    }}>{`Disabled Checkboxes`}</h2>
    <p>{`Disabled checkboxes and radios are supported. The disabled attribute will apply a lighter color to help indicate the input’s state.`}</p>
    <h3 {...{
      "id": "examples-of-documentation---disabled-checkboxes"
    }}>{`Examples oF Documentation - Disabled Checkboxes`}</h3>
    <pre><code parentName="pre" {...{}}>{`<div class="form-check">
    <input class="form-check-input" type="checkbox" name="exampleCustomCheckbox1" id="customCheckbox3" value="option3" disabled>
    <label class="form-check-label" for="customCheckbox3">
        Disabled checkbox
    </label>
</div>
`}</code></pre>
    <h3 {...{
      "id": "default-stacked"
    }}>{`Default (stacked)`}</h3>
    <p>{`By default, any number of checkboxes and radios that are immediate sibling will be vertically stacked and appropriately spaced with `}<inlineCode parentName="p">{`.form-check`}</inlineCode>{`.`}</p>
    <h3 {...{
      "id": "examples-of-documentation---default-stacked"
    }}>{`Examples oF Documentation - Default (stacked)`}</h3>
    <pre><code parentName="pre" {...{}}>{`<div class="form-check">
    <input class="form-check-input" type="checkbox" value="" id="defaultCheck1">
    <label class="form-check-label" for="defaultCheck1">
        Default checkbox
    </label>
</div>
<div class="form-check">
    <input class="form-check-input" type="checkbox" value="" id="defaultCheck2" disabled>
    <label class="form-check-label" for="defaultCheck2">
        Disabled checkbox
    </label>
</div>
`}</code></pre>
    <h3 {...{
      "id": "inline"
    }}>{`Inline`}</h3>
    <p>{`Group checkboxes or radios on the same horizontal row by adding `}<inlineCode parentName="p">{`.form-check-inline`}</inlineCode>{` to any `}<inlineCode parentName="p">{`.form-check`}</inlineCode>{`.`}</p>
    <h3 {...{
      "id": "examples-of-documentation---inline"
    }}>{`Examples oF Documentation - Inline`}</h3>
    <pre><code parentName="pre" {...{}}>{`<div class="form-check form-check-inline">
    <input class="form-check-input" type="checkbox" id="inlineCheckbox1" value="option1">
    <label class="form-check-label" for="inlineCheckbox1">1</label>
</div>
<div class="form-check form-check-inline">
    <input class="form-check-input" type="checkbox" id="inlineCheckbox2" value="option2">
    <label class="form-check-label" for="inlineCheckbox2">2</label>
</div>
<div class="form-check form-check-inline">
    <input class="form-check-input" type="checkbox" id="inlineCheckbox3" value="option3" disabled>
    <label class="form-check-label" for="inlineCheckbox3">3 (disabled)</label>
</div>
`}</code></pre>
    <h3 {...{
      "id": "without-labels"
    }}>{`Without labels`}</h3>
    <p>{`Add `}<inlineCode parentName="p">{`.position-static`}</inlineCode>{` to inputs within `}<inlineCode parentName="p">{`.form-check`}</inlineCode>{` that don’t have any label text. Remember to still provide some form of label for assistive technologies (for instance, using `}<inlineCode parentName="p">{`aria-label`}</inlineCode>{`).`}</p>
    <h3 {...{
      "id": "examples-of-documentation---radio-without-labels"
    }}>{`Examples oF Documentation - Radio without Labels`}</h3>
    <pre><code parentName="pre" {...{}}>{`<div class="form-check">
    <input class="form-check-input position-static" type="checkbox" id="blankCheckbox" value="option1" aria-label="...">
</div>
`}</code></pre>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
