/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */


const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1 {...{
      "id": "lines--borders"
    }}>{`Lines & Borders`}</h1>
    <p>{`All borders, horizontal and vertical dividers are `}<inlineCode parentName="p">{`light-gray`}</inlineCode>{` with a stroke of 1px. All element outlines have a corner radius value of zero unless noted otherwise.`}</p>
    <h2 {...{
      "id": "border"
    }}>{`Border`}</h2>
    <p>{`Use border utilities to add or remove an element's borders. Choose class `}<inlineCode parentName="p">{`.border`}</inlineCode>{` for all borders or specify a specific side as noted below.`}</p>
    <h3 {...{
      "id": "example-additive"
    }}>{`Example: Additive`}</h3>
    <pre><code parentName="pre" {...{}}>{`<div class="bd-example-border-utils">
    <span class="border"></span>
    <span class="border-top"></span>
    <span class="border-right"></span>
    <span class="border-bottom"></span>
    <span class="border-left"></span>
</div>
`}</code></pre>
    <h3 {...{
      "id": "example-subtractive"
    }}>{`Example: Subtractive`}</h3>
    <pre><code parentName="pre" {...{}}>{`<div>
    <span class="border-0"></span>
    <span class="border-top-0"></span>
    <span class="border-right-0"></span>
    <span class="border-bottom-0"></span>
    <span class="border-left-0"></span>
</div>
`}</code></pre>
    <h2 {...{
      "id": "border-colour"
    }}>{`Border Colour`}</h2>
    <p>{`Change the border colour using utilities built on our colour theme.`}</p>
    <pre><code parentName="pre" {...{}}>{`<div>
    <span class="border border-primary"></span>
    <span class="border border-secondary"></span>
    <span class="border border-success"></span>
    <span class="border border-danger"></span>
    <span class="border border-warning"></span>
    <span class="border border-info"></span>
    <span class="border border-light"></span>
    <span class="border border-dark"></span>
    <span class="border border-white"></span>
</div>
`}</code></pre>
    <h2 {...{
      "id": "border-radius"
    }}>{`Border Radius`}</h2>
    <p>{`Add classes to an element to easily round its corners.`}</p>
    <h3 {...{
      "id": "border-radius-and-shape-characteristics"
    }}>{`Border Radius and Shape Characteristics`}</h3>
    <pre><code parentName="pre" {...{}}>{`<div>
    <img src="..." alt="..." class="rounded">
    <img src="..." alt="..." class="rounded-top">
    <img src="..." alt="..." class="rounded-right">
    <img src="..." alt="..." class="rounded-bottom">
    <img src="..." alt="..." class="rounded-left">
    <img src="..." alt="..." class="rounded-circle">
    <img src="..." alt="..." class="rounded-pill">
    <img src="..." alt="..." class="rounded-0">
<div>
`}</code></pre>
    <h2 {...{
      "id": "lines"
    }}>{`Lines`}</h2>
    <h3 {...{
      "id": "vertical-lines"
    }}>{`Vertical Lines`}</h3>
    <p>{`Use line utilities to quickly style lines. This can be done by using border-left or border-right and adjusting its length, and size.`}</p>
    <pre><code parentName="pre" {...{}}>{`<style>
.vl {
  border-left: 1px solid #D9D9D6;
  height: 500px;
}
</style>

<div class="vl"></div>
`}</code></pre>
    <h3 {...{
      "id": "centered-vertical-line"
    }}>{`Centered Vertical Line`}</h3>
    <pre><code parentName="pre" {...{}}>{`<style>
.vl {
  border-left: 6px solid #D9D9D6;
  height: 500px;
  position: absolute;
  left: 50%;
  margin-left: -3px;
  top: 50vh;
}
</style>
<div class="vl"></div>
`}</code></pre>
    <h3 {...{
      "id": "horizontal-line"
    }}>{`Horizontal Line`}</h3>
    <p>{`Create horizontal lines by using the `}<inlineCode parentName="p">{`<hr>`}</inlineCode>{` tag. `}</p>
    <pre><code parentName="pre" {...{}}>{`<hr>
`}</code></pre>
    <p>{`You can also customize the `}<inlineCode parentName="p">{`<hr>`}</inlineCode>{` tag to adjust the line weight as shown below.`}</p>
    <pre><code parentName="pre" {...{}}>{`<style>
.hr {
    display: block;
    height: 1px;
    border: 0;
    border-top: 1px solid #D9D9D6;
    margin: 1em 0;
    padding: 0;
}
</style>
<div class="hr"></div>
`}</code></pre>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
